    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello DashboardController!{% endblock %}

{% block bodyinicio %}
{{ parent() }}
{% endblock %}

{% block body %}

    <section class=\"section\">
        <div class=\"cont-center\">
            <div class=\"cont-flex\" data-inner=\"1\">
                <div class=\"item-flex\">
                    <p class=\"bx-titulo h2\">Por favor inicia sesión!</p>
                </div>
            </div>
            <div class=\"cont-flex\" data-inner=\"2\">
                <div class=\"item-flex\">
                    <div class=\"fondo-claro\">
                        <form method=\"post\">
                            <div>
                                {% if error %}
                                    <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                                {% endif %}

                                {% if app.user %}
                                    <div class=\"mb-3\">
                                        Tu has iniciado sesión como {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Cerrar sesión</a>
                                    </div>
                                {% endif %}
                                <div>
                                    <label for=\"inputEmail\">Email</label>
                                </div>
                                <div>
                                    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
                                </div>
                                <div>
                                    <label for=\"inputPassword\">Password</label>
                                </div>
                                <div>
                                    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>
                                </div>

                                <div>
                                    <input type=\"hidden\" name=\"_csrf_token\"
                                        value=\"{{ csrf_token('authenticate') }}\" >
                                </div>

                                {#
                                    Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
                                    See https://symfony.com/doc/current/security/remember_me.html

                                    <div class=\"checkbox mb-3\">
                                        <label>
                                            <input type=\"checkbox\" name=\"_remember_me\"> Remember me
                                        </label>
                                    </div>
                                #}

                                <button class=\"btn btn-lg btn-primary\" type=\"submit\">
                                    Iniciar sesión
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </section>

{% endblock %}

{% block bodyfin %}
    {{ parent() }}
{% endblock %}", "security/login.html.twig", "/var/www/symfony/hmg-prueba/templates/security/login.html.twig");
